package String;

public class LongestRepeatingCharsReplacement {
    private static int findLongest(String s, int k){
        int n = s.length();

        int maxLen = -1;
        for(int i = 0; i < n; i++) {
            int[] freq = new int[26];
            int maxFreq = 0;
            for(int j = i; j < n; j++){
                char curr = s.charAt(j);
                freq[curr - 'A']++;

                maxFreq = Math.max(maxFreq,freq[curr-'A']);

                int replacements = (j-i+1) - maxFreq;

                if(replacements <= k){
                    maxLen = Math.max(j-i+1, maxLen);
                }else{
                    break;
                }
            }
        }

        return maxLen;
    }
    //ABAB
    public static void main(String[] args) {
        String s = "";
        int k = 1; //OP : 4, replace 2 BBs with A s will become AAAA

        int ans = findLongest(s, k);

        System.out.println(ans);

    }
}
